Item.method_code:
Innovator inn = this.getInnovator();
try
{
    Item createCompCalOutput = inn.newItem("","get");
    createCompCalOutput.setProperty("id",this.getID());
    createCompCalOutput.setProperty("keyed_name",this.getProperty("keyed_name",""));
    if(this.getProperty("type","") == "pmi_ComplianceReplicaBOM")
    {
        createCompCalOutput.setProperty("ReplicatedBomPartCode",this.getProperty("pmi_PartCode",""));
        createCompCalOutput.setType("pmi_ComplianceReplicaBOM");
    }
        
    Item createCompCalOutput1 = CLIPP_SDK.ComplianceCalc.pmi_CreateCompCalculationOutput(inn,createCompCalOutput,CCO);
    if(createCompCalOutput1.isError())
    {
        return inn.newError("Error in CLIPP SDK " + createCompCalOutput1.getErrorString());
    }
}

catch(Exception e)
{
    throw new Exception("Method name : pmi_CreateCompCalculationOutput, Error: " + e.ToString());
}
return this;
