Item.method_code:
const batchAttributes = {
    'pmi_current_quantity': 'Current Quantity',
    'pmi_expected_quantity': 'Expected Quantity',
    'pmi_produced_delivered_quantity': 'Produced / Delivered Quantity',
    'pmi_released_quantity': 'Released Quantity'
};
for (const batchAttribute in batchAttributes) {
    const getValue = document.getElementsByName(batchAttribute)[1].value;
    if (getValue) {
        validateValues(getValue, batchAttribute, batchAttributes[batchAttribute]);
    }
}

function validateValues(getValue, propertyName, propertyLabel) {
    if (getValue < 0) {
        document.getElementsByName(propertyName)[1].value = '';
        return top.aras.AlertError('Negative values are not allowed for ' + propertyLabel + '. Please enter the number greater than zero to proceed further.');
    }
    if (isNaN(getValue)) {
        document.getElementsByName(propertyName)[1].value = '';
        return top.aras.AlertError('Non Numeric values are not allowed for ' + propertyLabel + '. Please Enter the valid number value to proceed further.');
    }
}
