Item.method_code:
let thisItem = document.thisItem;
const typeOfPanel = thisItem.getProperty("pmi_type_of_panel");
const mantypeOfExtPanel = getFieldByName("mandatory_typeOfExtPanel");
const extConsumersOther = getFieldByName("mandatory_extConsumersOther");
const martkes = getFieldByName("mandatory_martkes");
const typeOfExtPanel = getFieldByName("pmi_type_external_panel");

if(typeOfPanel === "External Panel")
{
    mantypeOfExtPanel.style.visibility = 'visible';
    extConsumersOther.style.visibility = 'visible';
    martkes.style.visibility = 'visible';
    typeOfExtPanel.style.visibility = 'visible';
}
else if(typeOfPanel === "internal (SEC - Lausanne)" || typeOfPanel === "Internal Panel (PMI employees within PMI premises)")
{
    mantypeOfExtPanel.style.visibility = 'hidden';
    extConsumersOther.style.visibility = 'hidden';
    martkes.style.visibility = 'hidden';
    typeOfExtPanel.style.visibility = 'hidden';
}
